import React from "react";
import styled from "styled-components";
import { MaterialCommunityIcons } from "@expo/vector-icons";

import {
  Container,
  Title,
  Button,
  Left,
  Right,
  Body,
  Icon,
  Text,
  Thumbnail,
} from "native-base";
import { View, StyleSheet, Image } from "react-native";

const ContentContainer = ({ navigation, result, type }) => {
  return (
    <View style={styles.container}>
      <ThumbNail type={type} source={{ uri: result.thumbNail }} />
      {type === "cat" || type === "movie" ? <Text>{result.title}</Text> : null}

      {type === "movie" ? (
        <View style={styles.trainerContainer}>
          <Thumbnail
            style={styles.trainerThumb}
            small
            source={{
              uri: result.trainerPic,
            }}
          />
          <Text style={{ fontSize: 12 }}>{result.trainer}</Text>
        </View>
      ) : type === "playlist" ? (
        <View style={styles.playListContainer}>
          <Text>{result.title}</Text>
          <View style={{ flexDirection: "row" }}>
            <MaterialCommunityIcons
              name="checkbox-multiple-blank-outline"
              size={15}
              color="black"
              style={{ marginRight: 5, alignItems: "center" }}
            />
            <Text>{result.movieLists.length}</Text>
          </View>
        </View>
      ) : null}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    marginTop: 10,
    marginBottom: 10,
    marginRight: 20,
  },
  trainerContainer: {
    flexDirection: "row",
    marginTop: 7,
    alignItems: "center",
  },
  trainerThumb: {
    width: 24,
    height: 24,
    marginRight: 10,
  },
  playListContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
});

const ThumbNail = styled.Image`
  width: ${({ type }) => (type === "cat" ? 170 : 150)};
  height: ${({ type }) => (type === "cat" ? 150 : 150)};
  border-radius: 4;
  margin-bottom: 5;
`;

export default ContentContainer;
